<?phpwhile($row = mysql_fetch_array( $result )) {	echo "<tr class='even'><div align='left'><td width='3%'>";	echo "<span style='color:#666666; font-size:8pt'>{$numero}</span></td><td width='30%'>";if ($row["vendas_orgao"] == "Exercito"){	echo $row['clients_nm']." </br>";	echo "<span style='color:#666666; font-size:8pt'>CPF: {$row['clients_cpf']} | Matr.: {$row['clients_prec_cp']}</span></td><td width='12%'>";}else{	if ($row['cliente_nome']){		echo $row['cliente_nome']." </br>";		echo "<span style='color:#666666; font-size:8pt'>CPF: {$row['cliente_cpf']}</span></td><td width='12%'>";}	else {		echo $row['clients_nm']." </br>";		echo "<span style='color:#666666; font-size:8pt'>CPF: {$row['clients_cpf']} | Matr.: {$row['clients_prec_cp']}</span></td><td width='12%'>";	}}	echo $row['vendas_orgao']."<br/>";	echo "<span style='color:#666666; font-size:8pt'>{$row['vendas_banco']}</span>";	if ($row['vendas_proposta']){echo "<br /><span style='color:#666666; font-size:8pt'>nº: {$row['vendas_proposta']}</span>";}	echo "</td><td width='11%'>";$vendas_valor = ($row['vendas_valor']>0) ? number_format($row['vendas_valor'], 2, ',', '.') : '0' ;	echo "R$ {$vendas_valor} </br>";	if ($row["vendas_base"]){$vendas_base = "| B".$row["vendas_base"];} else {$vendas_base = "";}$result_tipo = mysql_query("SELECT * FROM sys_vendas_tipos WHERE tipo_id = " . $row['vendas_tipo_contrato'] . ";")or die(mysql_error());$row_tipo = mysql_fetch_array( $result_tipo );	echo "<span style='color:#666666; font-size:8pt'>{$row_tipo['tipo_nome']}{$vendas_base}</span></td><td width='21%'>"; $result_user = mysql_query("SELECT name FROM jos_users WHERE id = " . $row['vendas_consultor'] . ";")or die(mysql_error());$row_user = mysql_fetch_array( $result_user );		echo $row_user['name']." </br>";		$yr=strval(substr($row["vendas_dia_venda"],0,4));		$mo=strval(substr($row["vendas_dia_venda"],5,2));		$da=strval(substr($row["vendas_dia_venda"],8,2));		$hr=strval(substr($row["vendas_dia_venda"],11,2));		$mi=strval(substr($row["vendas_dia_venda"],14,2));		$data_venda = date("d/m/Y H:i:s", mktime ($hr,$mi,0,$mo,$da,$yr));		echo "<span style='color:#666666; font-size:8pt'>{$data_venda}</span></td><td width='15%'>"; $result_status = mysql_query("SELECT * FROM sys_vendas_status WHERE status_id = " . $row['vendas_status'] . ";")or die(mysql_error());$row_status = mysql_fetch_array( $result_status );if ($row["vendas_status"] > 0){echo "<span style='color:#666666; font-size:8pt'>{$row_status['status_nm']}</span>";}else{echo "<span style='color:#666666; font-size:8pt'>Enviada p/ implantação</span>";}	echo "<img src='sistema/imagens/status_{$row['vendas_status']}.png'>";if (($row['vendas_dia_pago']) && ($row['vendas_dia_pago'] != "0000-00-00")){	$vendas_dia_pago = implode(preg_match("~\/~", $row['vendas_dia_pago']) == 0 ? "/" : "-", array_reverse(explode(preg_match("~\/~", $row['vendas_dia_pago']) == 0 ? "-" : "/", $row['vendas_dia_pago'])));	$pagamento = $vendas_dia_pago." | ".$row['vendas_mes'];}else{if ($row['vendas_status'] == "8"){$pagamento = "Data não informada";}else{$pagamento = "Ainda não paga";}}	echo "<img src='sistema/imagens/contrato_{$row['vendas_contrato_fisico']}.png'>";	if ($row['vendas_tipo_contrato'] == "3") {echo " <img src='sistema/imagens/contrato_port_{$row['vendas_contrato_fisico2']}.png'>";}	echo " | <span style='color:#666666; font-size:8pt'>{$pagamento}</span></td><td width='5%'>"; 	echo "<span style='color:#666666; font-size:8pt'>{$row['vendas_id']}</span>";	echo "</td></div></tr>"; $exibindo = $exibindo + 1;$numero = $numero + 1;}?>